<?php if(0 == $follow_state['following']): ?><span event-node="doFollow" event-args="uid=<?php echo ($fid); ?>&uname=<?php echo ($uname); ?>&following=<?php echo ($follow_state['following']); ?>&follower=<?php echo ($follow_state['follower']); ?>&refer=<?php echo ($refer); ?>&isrefresh=<?php echo ($isrefresh); ?>" href="<?php echo U('public/Follow/doFollow', array('fid'=>$fid));?>" class="btns-red"><i class="ico-add"></i>关注</span>
<?php else: ?>
	<span event-node="unFollow" event-args="uid=<?php echo ($fid); ?>&uname=<?php echo ($uname); ?>&following=<?php echo ($follow_state['following']); ?>&follower=<?php echo ($follow_state['follower']); ?>&refer=<?php echo ($refer); ?>&isrefresh=<?php echo ($isrefresh); ?>" href="<?php echo U('public/Follow/unFollow', array('fid'=>$fid));?>" class="btns-gray"><i class="ico-already"></i>已关注</span><?php endif; ?>